def process_orders(self, market, orders):
    for order in orders:
        trade = Trade(
            market.market_book.market_id,
            order.selection_id,
            order.handicap,
            self,
        )
        offset_order = trade.create_order(
            side="LAY", order_type=LimitOrder(lay, stake),
        )
        self.place_order(market, offset_order)
        trade.offset_orders.append(order)
