class ClosedMarketLogging(LoggingControl):
    NAME = "CLOSED_MARKET_LOGGING_CONTROL"

    def __init__(self, *args, **kwargs):
        super(ClosedMarketLogging, self).__init__(*args, **kwargs)

    def _process_cleared_orders_meta(self, event):

        orders = event.event

        if len(orders) > 0:
            market_id = orders[0].market_id
            simulated = orders[0]._simulated

            if simulated is True:
                file_path = market_closed_report_path + '/' + str(market_id) + '_simulated_order_data.json'
            else:
                file_path = market_closed_report_path + '/' + str(market_id) + '_order_data.json'

            check_and_create_json(file_path, [format_betfair_order(order) for order in orders])
