def get_nearest_price(price, cutoffs=CUTOFFS):
    if price &lt;= MIN_PRICE:
        return MIN_PRICE
    if price &gt; MAX_PRICE:
        return MAX_PRICE
    price = as_dec(price)
    for cutoff, step in cutoffs:
        if price &lt; cutoff:
            break
    step = as_dec(step)
    return float((price * step).quantize(2, ROUND_HALF_UP) / step)
