def process_orders(self, market, orders):
    if market.market_book.status not in ['CLOSED', 'SUSPENDED']:
        for order in orders:
            if order.elapsed_seconds and order.status == OrderStatus.EXECUTABLE and order.elapsed_seconds > 10:
                if order.side == 'BACK':
                    lay_price = float(str(get_market_notes(market, order.selection_id)).split(",")[1])
                    if order.order_type.price != lay_price:
                        self.replace_order(market, order, lay_price)
                else:
                    back_price = float(str(get_market_notes(market, order.selection_id)).split(",")[0])
                    if order.order_type.price != back_price:
                        self.replace_order(market, order, back_price)
