def get_context(self, market_id: str = None) -> dict:
    if "AB" in self.context:
        _context = self.context["AB"]
        ab_context = self._get_rand_context(market_id, _context)
        context = {}
        for k, v in self.context.items():
            if k == "AB":
                continue
            if k in ab_context:
                context[k] = ab_context.pop(k)
            else:
                context[k] = v
        for k, v in ab_context.items():
            context[k] = v
        return context
    else:
        return self.context

@staticmethod
def _get_rand_context(market_id: str, context: List[Dict]) -> dict:
    id_ = int(market_id[2:])
    r = (0.5 + _INVERSE_GOLDEN_RATIO * id_) % 1
    return context[int(r // (1 / len(context)))].copy()

