import bisect

next_ = dict(zip(BETFAIR_TICKS, BETFAIR_TICKS[1:]))
previous_ = dict(zip(BETFAIR_TICKS[1:], BETFAIR_TICKS))


def next(tick):
    return next_[float(tick)]


def previous(tick):
    return previous_[float(tick)]


def round_down(price):
    return BETFAIR_TICKS[bisect.bisect(BETFAIR_TICKS, price) - 1]


def round_up(price):
    return BETFAIR_TICKS[bisect.bisect_left(BETFAIR_TICKS, price)]


def round(price):
    ceiling = round_up(price)
    floor = previous(ceiling)
    if price - floor < ceiling - price:
        return floor
    else:
        return ceiling
